def count_x_mas_patterns(grid):
    if not grid:
        return 0
    
    rows = len(grid)
    cols = len(grid[0])
    count = 0
    
    def is_valid_position(x, y):
        return 0 <= x < rows and 0 <= y < cols
    
    def check_diagonal(center_row, center_col, top_left_to_bottom_right=True):
        chars = []
        
        if top_left_to_bottom_right:
            positions = [
                (center_row - 1, center_col - 1),
                (center_row, center_col),
                (center_row + 1, center_col + 1)
            ]
        else:
            positions = [
                (center_row - 1, center_col + 1),
                (center_row, center_col),
                (center_row + 1, center_col - 1)
            ]
            
        for row, col in positions:
            if not is_valid_position(row, col):
                return False
            chars.append(grid[row][col])
            
        pattern = ''.join(chars)
        return pattern in ["MAS", "SAM"]
    
    for i in range(rows):
        for j in range(cols):
            if grid[i][j] == 'A':
                if (check_diagonal(i, j, True) and
                    check_diagonal(i, j, False)):
                    count += 1
    
    return count

